import React, { Component } from 'react'
import '../Styles/Page.scss';


export default class Page extends Component {
    constructor(){
        super();
        this.input = React.createRef();
        this.state = { 
            words:[
            'one',
            'two',
            'three',
            'four',
            'five'
        ],
            input: '',
            score:0,
        }
    };
    handleChange = (e) =>{
        this.setState({
            input:e.target.value
        })
    };
    clearInput= () => {
        this.setState({
            input:''
        })
    };
    handlekey = (e) => {
        e.preventDefault()
        let inputWord = this.input.current.value;
        console.log(inputWord);
        let newArray = this.state.words.filter(e => e !== inputWord);
            this.setState({
                    words: newArray, 
                }) 
                this.clearInput();

              console.log(newArray)  
        if(this.state.words.indexOf(inputWord) > 0){
            this.setState({
                score: this.state.score+1,
            })
        }       
            };

    render() {      
    return (
      <div className='container'>
        {this.state.words.map(item =>{
            return <h1 className = {item}>
                        {item}
                    </h1>

        })}
        <div className='score'>
            Score:{this.state.score}
        </div>
        <form className='search' onSubmit={(event)=>this.handlekey(event)} onChange={this.handleChange}>
            <input type ="reset" type='text' ref={this.input} value={this.state.input} />
        </form>
      </div>
    )
  }
}

