            'three'
        ];

        words.map(item =>{
            return <h1>{item}</h1>
        })

        console.log(words)
    return (
      <div>
        {words.map(item =>{
            return <h1>{item}</h1>
        })}
        <input type='text' />
      </div>
    )
  }
}

